df = df_raw

int_cols = ["ID","VerNum","DefID","DefVerN","AttrID","AttrType","CustomID","EntryNum","ParentKeyID","KeyID","ValInt"]
for c in int_cols:
    if c in df.columns:
        df = df.withColumn(c, F.col(c).cast("int"))

if "ValReal" in df.columns:
    df = df.withColumn("ValReal", F.col("ValReal").cast("decimal(28,14)"))

if "ValDate" in df.columns:
    # if ValDate has different formats we can adjust later
    df = df.withColumn("ValDate", F.to_timestamp("ValDate"))

df.printSchema()
df.show(5, truncate=False)
