int_cols = ["ID","VerNum","DefID","DefVerN","AttrID","AttrType","CustomID","EntryNum","ParentKeyID","KeyID","ValInt"]
for c in int_cols:
    if c in df_ll.columns:
        df_ll = df_ll.withColumn(c, F.col(c).cast("int"))

if "ValReal" in df_ll.columns:
    df_ll = df_ll.withColumn("ValReal", F.col("ValReal").cast("decimal(28,14)"))

if "ValDate" in df_ll.columns:
    df_ll = df_ll.withColumn("ValDate", F.to_timestamp("ValDate"))
