date_cols = [c for c in df2.columns if c not in ["legacyCaseId", "legacyIdField"]]

def to_ts(c):
    # Most of your values look like: 2014-01-21 00:00:00
    # Use explicit format first (fast + reliable), then fallback.
    return F.coalesce(
        F.to_timestamp(F.col(c), "yyyy-MM-dd HH:mm:ss"),
        F.to_timestamp(F.col(c), "yyyy-MM-dd"),
        F.to_timestamp(F.col(c))  # last resort
    )

df3 = df2
for c in date_cols:
    df3 = df3.withColumn(c, to_ts(c))
