NAMESPACE_UUID = uuid.UUID("6ba7b810-9dad-11d1-80b4-00c04fd430c8")

@F.udf(returnType=T.StringType())
def uuid5_from_caseid(case_id: str):
    if case_id is None:
        return None
    return str(uuid.uuid5(NAMESPACE_UUID, case_id.lower()))

def money_to_decimal(col):
    # handles: 3284.24, £3,284.24, " 3284.24 "
    cleaned = F.regexp_replace(F.trim(col.cast("string")), r"[£,\s]", "")
    cleaned = F.regexp_replace(cleaned, r"[^0-9\.\-]", "")
    return cleaned.cast(T.DecimalType(18, 2))
