from pyspark.sql.types import DateType, TimestampType

def apply_ancient_datetime_filter(df, cutoff="1900-01-01"):
    datetime_cols = [
        f.name for f in df.schema.fields
        if isinstance(f.dataType, (TimestampType, DateType))
    ]

    if not datetime_cols:
        return df

    filter_expr = " AND ".join([
        f"({c} IS NULL OR {c} >= '{cutoff}')"
        for c in datetime_cols
    ])

    return df.filter(filter_expr)

# Apply before timezone conversion
filtered_df = apply_ancient_datetime_filter(view_df)
