if not spark.catalog.tableExists(full_table_name):
    print(f"Table {full_table_name} does not exist. Creating table...")
    mssparkutils.notebook.run(
        "create_table_from_schema",
        600,
        params
    )
    print(f"created table {full_table_name} successfully  ...")
else:
    print(f"Table {full_table_name} already exists. Skipping table creation.")

if not error_message:
    try:
        insert_count = df_nsip_meeting.count()
        print(insert_count)

        df_nsip_meeting.write.mode("overwrite").option("path", table_location).format("parquet").saveAsTable(full_table_name)
        print(f"Written to {full_table_name}")

        end_exec_time = datetime.now()

    except Exception as e:
        error_message = f"Error appending data to the curated layer {full_table_name} table : {str(e)[:800]}"
        end_exec_time = datetime.now()
