def _seed_col(df):
    cols = [c for c in ['Staff Number','PersNo','PersNo.','Personnel Number','Employee ID','EmployeeID'] if c in df.columns]
    return F.coalesce(*[F.col(c).cast('string') for c in cols]) if cols else F.lit('seed')

def mask_keep_first_last_col(col):
    return F.when(col.isNull(), None).otherwise(F.regexp_replace(col.cast('string'), r'(?<=.).(?=.$)', '*'))

@F.udf('string')
def mask_fullname_udf(v):
    if v is None:
        return None
    parts = [p for p in str(v).split() if p]
    def m(p):
        p = str(p)
        if len(p) <= 2:
            return p
        return p[0] + ('*' * (len(p) - 2)) + p[-1]
    return ' '.join(m(p) for p in parts)

def random_int_from_seed(seed, min_value, max_value):
    return (F.abs(F.hash(seed)) % (max_value - min_value + 1)) + F.lit(min_value)

def random_date_from_seed(seed, start='1955-01-01', end='2005-12-31'):
    start_date = F.to_date(F.lit(start))
    end_date = F.to_date(F.lit(end))
    days = F.datediff(end_date, start_date)
    offset = F.abs(F.hash(seed)) % days
    return F.date_add(start_date, offset.cast('int'))

@F.udf('string')
def mask_email_with_persno_udf(email, pers_no, is_lm):
    try:
        if email is None:
            return None
        s = str(email)
        local = s.split('@')[0]
        if len(local) <= 2:
            masked_local = local
        else:
            masked_local = local[0] + ('*' * (len(local) - 2)) + local[-1]
        return f"{masked_local}@#PINS.com"
    except Exception:
        return None

@F.udf('string')
def generate_random_ni_number_udf(seed_str):
    letters = 'ABCDEFGHJKLMNPQRSTUVWXYZ'
    first = _rand.choice(letters)
    second = _rand.choice(letters)
    digits = ''.join(str(_rand.randint(0,9)) for _ in range(6))
    last = _rand.choice('ABCD')
    return f"{first}{second}{digits}{last}"

def apply_anonymisation_rules(df, sensitive_cols, classification):
    seed = _seed_col(df)
    out = df
    exists = set(df.columns)
    staff_col_name = next((c for c in ['Staff Number','PersNo','PersNo.','Personnel Number','Employee ID','EmployeeID'] if c in df.columns), None)
    staff_col = F.col(staff_col_name) if staff_col_name else seed
    # Ensure we have iterable of dicts with 'column_name' and 'classifications'
    if not isinstance(sensitive_cols, list):
        return out
    for item in sensitive_cols:
        if not isinstance(item, dict):
            continue
        col = item.get('column_name')
        if not col or col not in exists:
            continue
        classes = set(item.get('classifications', []) or [])
        # consider only classes in the provided classification allowlist
        if not any(c in classification for c in classes):
            continue
        is_lm = 'line manager' in col.lower()
        # Precedence-based application
        if ('NI Number' in classes) or ('PotentialID' in classes) or ('Potential ID' in classes):
            out = out.withColumn(col, generate_random_ni_number_udf(staff_col.cast('string')))
            continue
        if 'MICROSOFT.PERSONAL.EMAIL' in classes or 'Email Address' in classes:
            out = out.withColumn(col, mask_email_with_persno_udf(F.col(col), staff_col.cast('string'), F.lit(is_lm)))
            continue
        if 'MICROSOFT.PERSONAL.NAME' in classes or 'First Name' in classes or 'Last Name' in classes:
            # If appears to be a full name field, obfuscate each part; otherwise mask after first two
            if 'name' in col.lower() and 'first' not in col.lower() and 'last' not in col.lower():
                out = out.withColumn(col, mask_fullname_udf(F.col(col)))
            else:
                out = out.withColumn(col, mask_keep_first_last_col(F.col(col)))
            continue
        if 'Birth Date' in classes or 'Date of Birth' in classes:
            out = out.withColumn(col, random_date_from_seed(seed))
            continue
        if "Person's Age" in classes or 'Employee Age' in classes:
            out = out.withColumn(col, random_int_from_seed(seed, 18, 70).cast('int'))
            continue
        if 'Annual Salary' in classes:
            out = out.withColumn(col, random_int_from_seed(seed, 20000, 100000).cast('int'))
            continue
    return out
