# Compile all rejected records

# Combine all rejection into a DataFrames
rejection_dfs = []

if ldfno_duplicates_count > 0:
    rejection_dfs.append(df_ldfno_duplicates)

if ldfno_format_errors_count > 0:
    rejection_dfs.append(df_ldfno_format_errors)

if date_error_dfs:
    rejection_dfs.extend(date_error_dfs)

# Union all rejections
if rejection_dfs:
    df_all_rejections = rejection_dfs[0]
    for i in range(1, len(rejection_dfs)):
        df_all_rejections = df_all_rejections.union(rejection_dfs[i])

    total_rejected_rows = df_all_rejections.count()
else:
    df_all_rejections = None
    total_rejected_rows = 0

logInfo(f"   Total rejection rows: {total_rejected_rows:,}")
