# Pipeline metadata defaults (prevents NameError when not injected)
try:
    PipelineName
except NameError:
    PipelineName = mssparkutils.runtime.context.get("currentPipelineName", "unknown_pipeline")

try:
    PipelineRunID
except NameError:
    PipelineRunID = mssparkutils.runtime.context.get("pipelineRunId", "unknown_run_id")

try:
    PipelineTriggerID
except NameError:
    PipelineTriggerID = ""

try:
    PipelineTriggerName
except NameError:
    PipelineTriggerName = ""

try:
    PipelineTriggerType
except NameError:
    PipelineTriggerType = ""

try:
    PipelineTriggeredbyPipelineName
except NameError:
    PipelineTriggeredbyPipelineName = ""

try:
    PipelineTriggeredbyPipelineRunID
except NameError:
    PipelineTriggeredbyPipelineRunID = ""
