# HTTP helpers

from urllib.parse import quote

def _get(url, headers=HDRS, timeout=60):
   r = requests.get(url, headers=headers, timeout=timeout)
   if not r.ok:
       raise Exception(f"HTTP {r.status_code} â€“ {r.text[:800]}")
   return r.json()

def get_entity_with_refs(guid: str):
   url = (
       f"{BASE}/entity/guid/{guid}"
       f"?minExtInfo=true&ignoreRelationships=false&api-version={API_VERSION}"
   )
   return _get(url)

def get_entity_classifications(guid: str):
   url = f"{BASE}/entity/guid/{guid}/classifications?api-version={API_VERSION}"
   try:
       data = _get(url)
       return data.get("list", []) or []
   except Exception:
       return []

def get_guid_by_unique_attrs(type_name: str, qualified_name: str) -> str:

   qn = quote(qualified_name, safe="")
   url = (f"{BASE}/entity/uniqueAttribute/type/{quote(type_name, safe='')}"
          f"?attr%3AqualifiedName={qn}&api-version={API_VERSION}")
   data = _get(url)
   # Atlas may return either 'entity' or the flattened attributes + guid
   if "entity" in data and "guid" in data["entity"]:
       return data["entity"]["guid"]
   if "guid" in data:
       return data["guid"]
   raise Exception(f"Could not resolve GUID for {type_name} :: {qualified_name}")

# Resolve asset GUID from provided inputs
resource_guid = ASSET_GUID
if not resource_guid:
    if ASSET_TYPE_NAME and ASSET_QUALIFIED_NAME:
        resource_guid = get_guid_by_unique_attrs(ASSET_TYPE_NAME, ASSET_QUALIFIED_NAME)
    else:
        raise Exception("Provide ASSET_GUID or (ASSET_TYPE_NAME and ASSET_QUALIFIED_NAME)")

entity_data = get_entity_with_refs(resource_guid)
