if not error_message:
    try:
        horizon_data = horizon_data.select(service_bus_data.columns)
        results = service_bus_data.union(horizon_data)

    except Exception as e:
        error_message = f"Error while aligning Horizon schema and merging with Service Bus dataset using union: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        results.write.format("delta").mode("Overwrite").option("overwriteSchema", "true").partitionBy("IsActive").saveAsTable(f"{spark_table_final}")
    except Exception as e:
        error_message = f"Error in ingesting delta table {spark_table_final}: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        df_calcs = df_calcs.withColumnRenamed(primary_key, f"temp_{primary_key}").withColumnRenamed("IngestionDate", "temp_IngestionDate")

    except Exception as e:
        error_message = f"Error while renaming columns in df_calcs for appeal document processing: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        columns = results.columns

        results = results.drop("AppealsDocumentMetadataID", "ValidTo", "Migrated", "IsActive")

    except Exception as e:
        error_message = f"Error while dropping calculation columns from results dataset for appeal documents: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        final_df = results.join(df_calcs, (df_calcs[f"temp_{primary_key}"] == results[primary_key]) & (df_calcs["temp_IngestionDate"] == results["IngestionDate"])).select(columns).distinct()
        final_df = final_df.drop(primary_key).drop_duplicates()

    except Exception as e:
        error_message = f"Error while joining calculation results and finalising appeal document dataset: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        insert_count = final_df.count()
        final_df.write.format("delta").mode("Overwrite").option("overwriteSchema", "true").partitionBy("IsActive").saveAsTable(f"{spark_table_final}")
        end_exec_time = datetime.now()

    except Exception as e:
        error_message = f"Error in ingesting delta table in the final step {spark_table_final}: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)
