if not error_message:
    try:
        hzn_src = spark.table(horizon_table)

        # Here we dedup case insensitive duplicate column names
        seen = {}
        exprs = []
        for n in hzn_src.columns:
            lc = n.lower()
            if lc in seen:
                continue
            seen[lc] = n
            exprs.append(F.col(n).alias(n))
        hzn_src = hzn_src.select(*exprs)

    except Exception as e:
        error_message = f"Error while loading Horizon table and deduplicating case-insensitive columns from {horizon_table}: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        # Here we derive/backfill caseReference from likely aliases
        def _col_or_none(name):
            return F.col(name).cast("string") if name in hzn_src.columns else F.lit(None)

        if "caseReference" in hzn_src.columns:
            hzn_src = hzn_src.withColumn(
                "caseReference",
                F.coalesce(
                    F.col("caseReference").cast("string"),
                    _col_or_none("casereference"),
                    _col_or_none("caseuniqueid"),
                    _col_or_none("caseid")
                )
            )
        else:
            hzn_src = hzn_src.withColumn(
                "caseReference",
                F.coalesce(
                    _col_or_none("casereference"),
                    _col_or_none("caseuniqueid"),
                    _col_or_none("caseid")
                )
            )

        # Here is the last chance to rename if caseReference is still missing
        if "caseReference" not in hzn_src.columns:
            if "caseuniqueid" in hzn_src.columns:
                hzn_src = hzn_src.withColumnRenamed("caseuniqueid", "caseReference")
            elif "casereference" in hzn_src.columns:
                hzn_src = hzn_src.withColumnRenamed("casereference", "caseReference")

        # Here we derive IngestionDate (Prefer ingested_datetime then expected_from else now)
        if "IngestionDate" not in hzn_src.columns:
            ing_col = (
                F.col("ingested_datetime")
                if "ingested_datetime" in hzn_src.columns else
                F.col("expected_from")
                if "expected_from" in hzn_src.columns else
                F.current_timestamp()
            )
            hzn_src = hzn_src.withColumn("IngestionDate", ing_col)

        # Here we add the "plumbing" to look like SB rows (the final IsActive is recomputed by SCD-2 later)
        hzn_aligned = (
            hzn_src
              .withColumn("AppealS78ID", F.lit(None).cast("bigint"))
              .withColumn("Migrated", F.lit("0"))
              .withColumn("ODTSourceSystem", F.lit("HORIZON"))
              .withColumn("ValidTo", F.lit(None).cast("string"))
              .withColumn("RowID", F.lit(""))
              .withColumn("IsActive", F.lit("N"))
        )

        if "enforcementAppealGroundsDetails" in hzn_aligned.columns:
            hzn_aligned = hzn_aligned.withColumn(
                "enforcementAppealGroundsDetails",
                F.expr("""
                  transform(enforcementAppealGroundsDetails, x ->
                    named_struct(
                      'appealGroundLetter', x.appealGroundLetter,
                      'groundForAppealStartDate', cast(x.groundForAppealStartDate as string),
                      'groundFacts', cast(null as string)
                    )
                  )
                """)
            )

    except Exception as e:
        error_message = f"Error while deriving caseReference/IngestionDate and applying Horizon alignment plumbing: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        # Here we align Horizon to SB schema (add missing columns and cast types)
        sb_schema = service_bus_data.schema
        for f in sb_schema:
            if f.name not in hzn_aligned.columns:
                hzn_aligned = hzn_aligned.withColumn(f.name, F.lit(None).cast(f.dataType))
            else:
                if str(hzn_aligned.schema[f.name].dataType) != str(f.dataType):
                    hzn_aligned = hzn_aligned.withColumn(f.name, F.col(f.name).cast(f.dataType))

        # Here we drop the duplicate names case insensitively
        seen = {}
        for c in list(hzn_aligned.columns):
            lc = c.lower()
            if lc in seen:
                hzn_aligned = hzn_aligned.drop(c)
            else:
                seen[lc] = c

        # Here we project to the exact SB column order
        proj = []
        lower_map = {c.lower(): c for c in hzn_aligned.columns}
        for f in sb_schema:
            if f.name in hzn_aligned.columns:
                proj.append(F.col(f.name).alias(f.name))
            elif f.name.lower() in lower_map:
                proj.append(F.col(lower_map[f.name.lower()]).cast(f.dataType).alias(f.name))
            else:
                proj.append(F.lit(None).cast(f.dataType).alias(f.name))
        hzn_aligned = hzn_aligned.select(*proj)

        # Here we union Service bus to Stage
        stg_df = service_bus_data.unionByName(hzn_aligned, allowMissingColumns=True).distinct()

        # Here we enforce non null key
        stg_df = stg_df.filter(F.col("caseReference").isNotNull())

    except Exception as e:
        error_message = f"Error while aligning Horizon to Service Bus schema, projecting columns, and building staged dataset: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)
