if not error_message:
    try:
        results = service_bus_data.union(horizon_data)

    except Exception as e:
        error_message = f"Error while merging Service Bus and Horizon datasets using union: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        logInfo(f"Writing {spark_table_final}")
        results.write.format("delta").mode("Overwrite").option("overwriteSchema", "true").partitionBy("IsActive").saveAsTable(f"{spark_table_final}")
        logInfo(f"Written {spark_table_final}")

    except Exception as e:
        error_message = f"Error in ingesting delta table {spark_table_final}: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        df_calcs =df_calcs.withColumnRenamed("representationId", "temp_representationId").withColumnRenamed("IngestionDate", "temp_IngestionDate")

    except Exception as e:
        error_message = f"Error while renaming columns in df_calcs for NSIP representation processing: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        columns = results.columns
        results = results.drop("NSIPRepresentaionID", "ValidTo", "Migrated", "IsActive")

    except Exception as e:
        error_message = f"Error while dropping calculation columns from results dataset: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        logInfo(f"Merging final {spark_table_final}")
        final_df = results.join(df_calcs, (df_calcs["temp_representationId"] == results["representationId"]) & (df_calcs["temp_IngestionDate"] == results["IngestionDate"])).select(columns)
        logInfo(f"Dropping dupes from {spark_table_final}")
        final_df = final_df.drop_duplicates()

    except Exception as e:
        error_message = f"Error while joining calculation results and removing duplicates for {spark_table_final}: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)
