if not error_message:
    try:
        results = service_bus_data.union(horizon_data)

    except Exception as e:
        error_message = f"Error while merging Service Bus and Horizon datasets using union: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        logInfo(f"Writing {spark_table_final}")
        results.write.format("delta").mode("Overwrite").option("overwriteSchema", "true").partitionBy("IsActive").saveAsTable(f"{spark_table_final}")
        logInfo(f"Written {spark_table_final}")

    except Exception as e:
        error_message = f"Error in ingesting delta table {spark_table_final}: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        df_calcs =df_calcs.withColumnRenamed("adviceid", "temp_adviceid").withColumnRenamed("IngestionDate", "temp_IngestionDate")

    except Exception as e:
        error_message = f"Error while renaming columns in df_calcs for NSIP s51 advice processing: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        columns = results.columns
        results = results.drop("NSIPAdviceID", "ValidTo", "Migrated", "IsActive")

    except Exception as e:
        error_message = f"Error while dropping calculation columns from results dataset for NSIP s51 advice: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        logInfo(f"Merging to {spark_table_final}")
        final_df = results.join(df_calcs, (df_calcs["temp_adviceid"] == results["adviceid"]) & (df_calcs["temp_IngestionDate"] == results["IngestionDate"])).select(columns)
        logInfo(f"Dropping duplicates from {spark_table_final}")
        final_df = final_df.drop_duplicates()

    except Exception as e:
        error_message = f"Error while merging calculation results and removing duplicates for NSIP s51 advice: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)

if not error_message:
    try:
        # Get deleted primary keys into a list
        deleted_ids = [row[0] for row in horizon_deleted.collect()]

        # Set IsActive to N if the primary key has been deleted from Horizon
        hrm_updated = final_df.withColumn(
            "IsActive", F.when(F.col("adviceId").isin(deleted_ids), "N").otherwise(F.col("IsActive"))
        )

        # Set ValidTo to null if IsActive = Y
        hrm_updated = hrm_updated.withColumn(
            "ValidTo", F.when(F.col("IsActive") == "Y", None).otherwise(F.col("ValidTo"))
        )   

        # Set ValidTo if IsActive = N to the IngestionDate + 1 day
        hrm_final = hrm_updated.withColumn(
            "ValidTo", F.when((F.col("ValidTo").isNull()) & (F.col("IsActive") == "N"), F.date_add(F.col("IngestionDate"), 1).cast("timestamp")).otherwise(F.col("ValidTo"))
        )

    except Exception as e:
        error_message = f"Error while applying Horizon delete logic and updating IsActive/ValidTo for NSIP s51 advice: {str(e)[:800]}"
        end_exec_time = datetime.now()
        print(error_message)
