# Check if the old table exists
if test_table_exists(db_name, old_table_name):
    print(f"Table {db_name}.{old_table_name} exists.")

    # Check if the new table already exists
    if test_table_exists(db_name, new_table_name):
        print(f"Table {db_name}.{new_table_name} already exists. Skipping rename operation.")
    else:
        print(f"Renaming {db_name}.{old_table_name} to {db_name}.{new_table_name}...")
        spark.sql(f"ALTER TABLE {db_name}.{old_table_name} RENAME TO {db_name}.{new_table_name}")
        print(f"Successfully renamed table to {db_name}.{new_table_name}")

        # Verify the rename was successful
        if test_table_exists(db_name, new_table_name):
            print(f"Verification: {db_name}.{new_table_name} exists.")
        else:
            print(f"ERROR: Failed to verify existence of {db_name}.{new_table_name}")
else:
    print(f"Table {db_name}.{old_table_name} does not exist. No action needed.")
