activityMessage.value:
@if(
    equals(
        trim(
            concat(
                coalesce(activity('Record horizon AIE document data')?.Error?.message, ''),
                coalesce(activity('Src to Raw')?.Error?.message, ''),
                coalesce(activity('Raw to Std')?.Error?.message, ''),
                coalesce(activity('Std to Hrm')?.Error?.message, '')
            )
        ),
        ''
    ),
    'Successfully',
    'Failed'
)

activityDuration.value:
@concat(
    string(div(div(sub(
        ticks(formatDateTime(utcNow(), 'yyyy-MM-dd HH:mm:sss')),
        ticks(formatDateTime(if(equals(pipeline().parameters.start_time, null), utcNow(), pipeline().parameters.start_time), 'yyyy-MM-dd HH:mm:sss'))
    ), 600000000), 60)), 'H:', 
    string(mod(div(sub(
        ticks(formatDateTime(utcNow(), 'yyyy-MM-dd HH:mm:sss')),
        ticks(formatDateTime(if(equals(pipeline().parameters.start_time, null), utcNow(), pipeline().parameters.start_time), 'yyyy-MM-dd HH:mm:sss'))
    ), 600000000), 60)), 'M:00s'
)

