value.value:
{
  "type": "message",
  "attachments": [
    {
      "contentType": "application/vnd.microsoft.card.adaptive",
      "content": {
        "type": "AdaptiveCard",
        "body": [
          {
            "type": "Container",
            "items": [
              {
                "type": "ColumnSet",
                "columns": [
                  {
                    "type": "Column",
                    "items": [
                      {
                        "type": "Image",
                        "style": "Person",
                        "url": "@{pipeline().parameters.Image}",
                        "size": "Small"
                      }
                    ],
                    "width": "auto"
                  },
                  {
                    "type": "Column",
                    "items": [
                      {
                        "type": "TextBlock",
                        "size": "large",
                        "weight": "Bolder",
                        "text": "@{pipeline().parameters.Message_title}",
                        "wrap": true,
                        "color": "@{variables('titleColour')}"
                      },
                      {
                        "type": "TextBlock",
                        "text": "@{pipeline().parameters.Message_subtitle}",
                        "spacing": "None",
                        "wrap": true,
                        "size": "small"
                      }
                    ],
                    "width": "stretch"
                  }
                ]
              },
              {
                "type": "FactSet",
                "facts": [
                  {
                    "title": "Pipeline RunId:",
                    "value": "@{pipeline().parameters.pipelineRunId}"
                  },
                  {
                    "title": "Activity name:",
                    "value": "@{pipeline().parameters.activityName}"
                  },
                  {
                    "title": "Activity status:",
                    "value": "@{pipeline().parameters.activityStatus}"
                  },
                  {
                    "title": "Pipeline duration:",
                    "value": "@{pipeline().parameters.activityDuration}"
                  },
                  {
                    "title": "Notification time (UTC):",
                    "value": "@{utcNow()}"
                  },
                  {
                    "title": "Data Factory name:",
                    "value": "@{pipeline().DataFactory}"
                  }
                ]
              },
              {
                "type": "TextBlock",
                "text": "@{pipeline().parameters.activityMessage}",
                "wrap": true,
                "spacing": "Medium"
              }
            ]
          }
        ],
        "actions": [
          {
            "type": "Action.OpenUrl",
            "title": "View pipeline run",
            "url": "@{concat('https://web.azuresynapse.net/en/monitoring/pipelineruns/',pipeline().parameters.pipelineRunId,'?workspace=/subscriptions/',pipeline().parameters.dataFactorySubscription,'/resourceGroups/',pipeline().parameters.dataFactoryResourceGroup,'/providers/Microsoft.Synapse/workspaces/',pipeline().DataFactory,'&filter.pipelinename=', pipeline().parameters.activityName)}",
            "style": "positive"
          }
        ],
        "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
        "version": "1.5"
      }
    }
  ]
}
